package com.aaa.entity;

import java.io.Serializable;
import java.util.Date;

//撤单记录表
public class RevokeIndent implements Serializable{

	private Integer rin_id;
	private String rin_cause;
	private Date rin_time;
	private Integer u_id;
	private String i_id;
	private Integer rin_state;
	public RevokeIndent() {
		super();
		// TODO Auto-generated constructor stub
	}
	public RevokeIndent(Integer rin_id, String rin_cause, Date rin_time,
			Integer u_id, String i_id, Integer rin_state) {
		super();
		this.rin_id = rin_id;
		this.rin_cause = rin_cause;
		this.rin_time = rin_time;
		this.u_id = u_id;
		this.i_id = i_id;
		this.rin_state = rin_state;
	}
	public Integer getRin_id() {
		return rin_id;
	}
	public void setRin_id(Integer rin_id) {
		this.rin_id = rin_id;
	}
	public String getRin_cause() {
		return rin_cause;
	}
	public void setRin_cause(String rin_cause) {
		this.rin_cause = rin_cause;
	}
	public Date getRin_time() {
		return rin_time;
	}
	public void setRin_time(Date rin_time) {
		this.rin_time = rin_time;
	}
	public Integer getU_id() {
		return u_id;
	}
	public void setU_id(Integer u_id) {
		this.u_id = u_id;
	}
	public String getI_id() {
		return i_id;
	}
	public void setI_id(String i_id) {
		this.i_id = i_id;
	}
	public Integer getRin_state() {
		return rin_state;
	}
	public void setRin_state(Integer rin_state) {
		this.rin_state = rin_state;
	}
	@Override
	public String toString() {
		return "RevokeIndent [rin_id=" + rin_id + ", rin_cause=" + rin_cause
				+ ", rin_time=" + rin_time + ", u_id=" + u_id + ", i_id="
				+ i_id + ", rin_state=" + rin_state + "]";
	}
	
	

}
